<template>
  <v-container fluid>
    <v-row>
      <v-col cols="12" md="6" class="d-none d-md-block">
        <div class="login-image"></div>
      </v-col>
      <v-col
        cols="12"
        md="6"
        class="d-flex flex-column align-self-center justify-center"
      >
        <div class="d-flex justify-center align-center">
          <v-img
            class="logo"
            src="data:image/jpeg;base64,"
            contain
          ></v-img>
        </div>
        <div class="pa-8">
          <h2 class="mb-6 text-center text-heading-6">Connexion</h2>
          <v-form @submit.prevent="handleLogin">
            <v-text-field
              v-model="email"
              outlined
              label="Email"
              prepend-icon="mdi-account"
              required
              :error-messages="emailErrors"
            ></v-text-field>
            <v-text-field
              v-model="password"
              outlined
              label="Mot de passe"
              prepend-icon="mdi-lock"
              :type="passwordVisible ? 'text' : 'password'"
              required
              :append-icon="passwordVisible ? 'mdi-eye-off' : 'mdi-eye'"
              @click:append="() => (passwordVisible = !passwordVisible)"
              :error-messages="passwordErrors"
            ></v-text-field>
            <v-btn color="primary" type="submit" class="mt-6" block>
              Se connecter
            </v-btn>
          </v-form>
        </div>
      </v-col>
    </v-row>
  </v-container>
</template>

<script>
export default {
  name: "LoginView",
  data() {
    return {
      password: "",
      email: "",
      emailErrors: [],
      passwordErrors: [],
      passwordVisible: false,
    };
  },
  methods: {
    handleLogin() {
      if (!this.email) {
        this.emailErrors.push("L'adresse e-mail est requise");
        return;
      } else if (!/.+@.+\..+/.test(this.email)) {
        this.emailErrors.push("L'adresse e-mail doit Ãªtre valide");
        return;
      } else {
        this.emailErrors = [];
      }

      if (!this.password) {
        this.passwordErrors.push("Le mot de passe est requis");
        return;
      } else {
        this.passwordErrors = [];
      }

      if (this.email && this.password) {
        this.$router.push("/home");
        // Call login API
      }
    },
  },
};
</script>

